#' @import backports
NULL

#' @importFrom utils packageName
load_from_file_ <- function(path = "tic.R", ..., mtime = file.mtime(path)) {
  env <- asNamespace(packageName())
  source_env <- new.env(parent = env)
  dsl <- create_dsl(envir = env)
  .dsl_storage$dsl <- dsl
  on.exit(.dsl_storage$dsl <- NULL, add = TRUE)
  source(path, local = source_env)
  dsl$get_stages()
}

#' Process a tic.R file
#'
#' Return a named list of stages generated by [source()]ing a file that uses the
#' tic [DSL].
#' The file is sourced only once per session, unless its modification timestamp
#' changes.
#'
#' @param path `[string]`\cr
#'   Path to the stage definition file, default: `"tic.R"`.
#' @param ... ignored
#' @param mtime `[POSIXt]`\cr
#'   For internal use, to detect changes to this file.
#'
#' @export
load_from_file <- memoise::memoise(load_from_file_)

#' tic's domain-specific language
#'
#' Functions to define stages and their constitutent
#' steps.
#'
#' @name DSL
NULL

#' @description
#' `get_stage()` returns a Stage object for a stage given by name.
#' This function only works when called by [load_from_file()].
#'
#' @param name `[string]`\cr
#'   The name for the stage.
#' @rdname DSL
#' @export
get_stage <- function(name) {
  get_current_dsl()$get_stage(name)
}

#' @description
#' `add_step()` adds a step to a stage, see [step_hello_world()]
#' and the links therein for available steps.
#'
#' @param stage `[Stage]`\cr
#'   A Stage object as returned by `get_stage()`.
#' @param step `[function]`\cr
#'   An object of class [TicStep], usually created by functions
#'   with the `step_` prefix like [step_hello_world()].
#' @rdname DSL
#' @export
add_step <- function(stage, step) {
  step_desc <- deparse(substitute(step), width.cutoff = 500, nlines = 1)

  tryCatch(
    step <- force(step),
    error = function(e) {
      stop("Error evaluating the step argument of add_step(), expected an object of class TicStep.\n",
        "Original error: ", conditionMessage(e),
        call. = FALSE
      )
    }
  )

  stopifnot(inherits(step, "TicStep"))

  stage$add_step(step, step_desc)
}

#' @description
#' `add_code_step()` is a shortcut for `add_step(step_run_code(...))`.
#'
#' @export
#' @inheritParams step_run_code
#' @rdname DSL
add_code_step <- function(stage, call = NULL, prepare_call = NULL) {
  call <- substitute(call)
  prepare_call <- substitute(prepare_call)
  step <- RunCode$new(.call = call, .prepare_call = prepare_call)
  stage$add_step(
    step,
    paste0(
      "step_run_code(",
      deparse(call, width.cutoff = 500, nlines = 1),
      if (!is.null(prepare_call)) {
        paste0(
          ", prepare_call = ",
          deparse(prepare_call, width.cutoff = 500, nlines = 1)
        )
      },
      ")"
    )
  )
}

#' @description
#' `do_package_checks()` adds default steps related to package checks
#' to the `"before_install"`, `"install"`, `"script"` and `"after_success"`
#' stages:
#'
#' @inheritParams step_rcmdcheck
#' @param codecov `[logical]`\cr
#'   Option to run `covr::codecov(quiet = TRUE)`. Defaults to `TRUE`, but
#'   can be controlled by an environment variable if using matrix in travis.
#' @rdname DSL
#' @export
#' @importFrom magrittr %>%
do_package_checks <- function(...,
                              warnings_are_errors = NULL,
                              notes_are_errors = NULL,
                              args = c("--no-manual", "--as-cran"),
                              build_args = "--force", error_on = "warning",
                              repos = getOption("repos"), timeout = Inf,
                              codecov = TRUE) {
  #' @description
  #' 1. [step_install_deps()] in the `"install"` stage, using the
  #'    `repos` argument.
  get_stage("install") %>%
    add_step(
      step_install_deps(repos = repos)
    )

  #' 1. [step_rcmdcheck()] in the `"script"` stage, using the
  #'    `warnings_are_errors`, `notes_are_errors`, `args`, and
  #'    `build_args` arguments.
  get_stage("script") %>%
    add_step(
      step_rcmdcheck(
        warnings_are_errors = warnings_are_errors,
        notes_are_errors = notes_are_errors,
        args = args,
        build_args = build_args,
        error_on = error_on,
        repos = repos,
        timeout = timeout
      )
    )

  #' 1. A call to [covr::codecov()] in the `"after_success"` stage (only for non-interactive CIs)
  if (codecov && !ci_is_interactive()) {
    get_stage("after_success") %>%
      add_code_step(covr::codecov(quiet = FALSE))
  }
}
#' @description
#' `do_pkgdown()` builds and optionally deploys a pkgdown site and adds default steps
#'   to the `"install"`, `"before_deploy"` and `"deploy"` stages:
#'
#' @inheritParams step_build_pkgdown
#' @inheritParams step_setup_push_deploy
#' @inheritParams step_do_push_deploy
#' @param build_only Build the pkgdown site but do not deploy it.
#'
#' @rdname DSL
#' @export
#' @importFrom magrittr %>%
do_pkgdown <- function(...,
                       build_only = FALSE,
                       orphan = FALSE,
                       checkout = TRUE,
                       repos = getOption("repos"),
                       path = ".", branch = NULL,
                       remote_url = NULL,
                       commit_message = NULL, commit_paths = ".") {

  #' @description
  #' 1. [step_install_deps()] in the `"install"` stage, using the
  #'    `repos` argument.
  get_stage("install") %>%
    add_step(step_install_deps(repos = repos))

  if (isTRUE(build_only)) {
  } else if (!ci_can_push()) {
    stop("Deployment is not possible because keys are not set. Try setting `id_rsa` using `travis::use_travis_deploy()`.")
  } else {

    #' 1. [step_setup_ssh()] in the `"before_deploy"` to setup the upcoming deployment.
    #' 1. [step_setup_push_deploy()] in the `"before_deploy"` stage.
    #' 1. [step_build_pkgdown()] in the `"deploy"` stage
    #' 1. [step_do_push_deploy()] in the `"deploy"` stage. By default, the deploy is done to the gh-pages branch.
    get_stage("before_deploy") %>%
      add_step(step_setup_ssh()) %>%
      add_step(step_setup_push_deploy(
        path = path, branch = branch,
        remote_url = remote_url, orphan = orphan, checkout = checkout
      ))
  }

  get_stage("deploy") %>%
    add_step(step_build_pkgdown(...))

  if (isTRUE(build_only)) {
    ci_cat_with_color("`build_only = TRUE` was set, skipping deployment.")
  } else {
    get_stage("deploy") %>%
      add_step(step_do_push_deploy(
        path = path, commit_message = commit_message, commit_paths = commit_paths
      ))
  }
}

#' Deprecated functions
#'
#' `add_package_checks()` has been replaced by [do_package_checks()].
#'
#' @inheritParams do_package_checks
#' @name Deprecated
#' @export
add_package_checks <- function(...,
                               warnings_are_errors = NULL,
                               notes_are_errors = NULL,
                               args = c("--no-manual", "--as-cran"),
                               build_args = "--force", error_on = "warning",
                               repos = getOption("repos"), timeout = Inf) {
  .Deprecated("do_package_checks")
  do_package_checks(
    ... = ...,
    warnings_are_errors = warnings_are_errors,
    notes_are_errors = notes_are_errors,
    args = args,
    build_args = build_args, error_on = error_on,
    repos = repos, timeout = timeout
  )
}

#' @importFrom magrittr %>%
DSL <- R6Class(
  "DSL",
  public = list(
    initialize = function() {
      stage_names <- c(
        "before_install",
        "install",
        "after_install",
        "before_script",
        "script",
        "after_success",
        "after_failure",
        "before_deploy",
        "deploy",
        "after_deploy",
        "after_script"
      )

      private$stages <- lapply(stats::setNames(nm = stage_names), Stage$new)
    },

    get_stage = function(name) {
      stage <- self$get_stages()[[name]]
      if (is.null(stage)) {
        stop("Unknown stage ", name, ".", call. = FALSE)
      }
      stage
    },

    get_stages = function() {
      private$stages
    }
  ),

  private = list(
    stages = NULL
  )
)

create_dsl <- function(envir = parent.frame()) {
  dsl <- DSL$new()
  parent.env(dsl) <- envir
  dsl
}

.dsl_storage <- new.env(parent = emptyenv())

# Initialized in .onLoad()

get_current_dsl <- function() {
  .dsl_storage$dsl
}
